import React, { useState } from "react";
import axios from "axios";
import "./Usersignup.css";
import NavbarHead from "./Navbar";

function Login(props) {
  const [eusername, setusername] = useState("");
  const [epassword, setpassword] = useState("");
  const [msg, setMessage] = useState("");

  const onChangeusername = (e) => setusername(e.target.value);
  const onChangepassword = (e) => setpassword(e.target.value);

  const handleSubmit = (evt) => {
    evt.preventDefault();

    console.log(`EMAIL: ${eemail}`);
    console.log(`PASS: ${epassword}`);

    const userlogininfo = {
      email: eemail,
      password: epassword,
    };

    axios
      .post("http://localhost:5000/user/login", userlogininfo)
      .then((res) => {
        console.log(res.data);
        localStorage.setItem("Key_Veriable", "USER");
        localStorage.setItem("useremail", res.data[0].email);
        localStorage.setItem("username", res.data[0].username);
        props.history.push("/stay");
      })
      .catch((err) => {
        console.log(err);
        setMessage("INVALID UID OR PASSWORD");
      });

    setemail("");
    setpassword("");
  };
  return (
    <div>
      <NavbarHead />
      <div className="login-box">
        <h2>Login</h2>
        <form  onSubmit={handleSubmit}>
          <div className="user-box">
            <input type="text" value={eusername}  onChange={onChangeusername} required></input>
            <label>Username</label>
            <div className="user-box">
              <input type="password"  value={epassword} onChange={onChangepassword} required></input>
              <label>Password</label>
            </div>
          </div>
          <input type="submit" value="LOGIN" className="btn btn-primary" />
            <span></span>
            <span></span>
            <span></span>
            <span></span>
            
          
        </form>
      </div>
    </div>
  );
}

export default Login;
