import React, { useState } from 'react'
import axios from "axios";
import NavbarHead from "./Navbar";
import "./Signup.css";

function Signup() {
  const [eusername, setusername] = useState("");
  const [eemail, setemail] = useState("");
  const [ephonenumber, setphonenumber] = useState("");
  const [epassword, setpassword] = useState("");
  const [msg, setMessage] = useState("");

  const onChangeusername = (e) => setusername(e.target.value);
  const onChangeemail = (e) => setemail(e.target.value);
  const onChangephonenumber = (e) => setphonenumber(e.target.value);
  const onChangepassword = (e) => setpassword(e.target.value);

  const handleSubmit = (evt) => {
    evt.preventDefault();
    console.log(`Form submitted:`);
    console.log(`NAME: ${eusername}`);
    console.log(`EMAIL: ${eemail}`);

    const userinfo = {
      username: eusername,
      email: eemail,
      phonenumber: ephonenumber,
      password: epassword,
    };

    axios.post("http://localhost:5000/user/Signuppasswordencrypt", userinfo).then((res) => {
      console.log(res.data);
      setMessage("SIGNUP SUCCESSFUL");
    });

    setusername("");
    setemail("");
    setphonenumber("");
    setpassword("");
  };

  return (
    <div className="bgimage">
      <NavbarHead />
      <div align="center">
        <h1 className="text-secondary">USER SIGNUP</h1>
        <h4 style={{ color: "brown" }}> {msg}</h4>
        <form onSubmit={handleSubmit}>
          <br />
          <input
            type="text"
            value={eusername}
            onChange={onChangeusername}
            placeholder=" USERNAME"
            className="w-25 text-center"
            required
          />
          <br />
          <br />
          <input
            type="text"
            value={eemail}
            onChange={onChangeemail}
            placeholder="EMAIL"
            className="w-25 text-center"
            required
          />
          <br />
          <br />
          <input
            type="number"
            value={ephonenumber}
            onChange={onChangephonenumber}
            placeholder="PHONENUMBER"
            className="w-25 text-center"
            required
          />
          <br />
          <br />
          <input
            type="number"
            value={epassword}
            onChange={onChangepassword}
            placeholder="PASSWORD"
            className="w-25 text-center"
            required
          />
          <br />
          <br />
          <input type="submit" value="SIGNUP" className="btn btn-primary" />
          
        </form>
      </div>
    </div>
  );
}

export default Signup;
